import 'package:freezed_annotation/freezed_annotation.dart';part 'top_categories_model.freezed.dart';part 'top_categories_model.g.dart';@freezedclass TopCategoriesModel with _$TopCategoriesModel {  @JsonSerializable(explicitToJson: true, fieldRename: FieldRename.snake)  const factory TopCategoriesModel(    bool? success,    String? message,    int? code,    TopCategoriesData? data,  ) = _TopCategoriesModel;  factory TopCategoriesModel.fromJson(Map<String, dynamic> json) =>      _$TopCategoriesModelFromJson(json);}@freezedclass TopCategoriesData with _$TopCategoriesData {  @JsonSerializable(explicitToJson: true, fieldRename: FieldRename.snake)  const factory TopCategoriesData(List<TopCategoriesItem>? data) =      _TopCategoriesData;  factory TopCategoriesData.fromJson(Map<String, dynamic> json) =>      _$TopCategoriesDataFromJson(json);}@freezedclass TopCategoriesItem with _$TopCategoriesItem {  @JsonSerializable(explicitToJson: true, fieldRename: FieldRename.snake)  const factory TopCategoriesItem(    String? title,    String? image,    String? slug,  ) = _TopCategoriesItem;  factory TopCategoriesItem.fromJson(Map<String, dynamic> json) =>      _$TopCategoriesItemFromJson(json);}