import '../../../di/di.dart';import '../models/branchs/filial_model.dart';import '../models/category/all_categories_model.dart';import '../models/product_items/product_items_model.dart';import '../models/slider/slider_model.dart';import '../models/top_categories/top_categories_model.dart';import '../models/top_products/top_products_model.dart';import '../remote/api_service.dart';import 'app_repository.dart';class AppRepositoryImpl extends AppRepository {  final api = getIt<ApiService>();  @override  Future<SliderModel> getSliderData() async {    final response = await api.getSliders();    return response;  }  @override  Future<AllCategoriesModel> getAllCategories() async {    final response = await api.getAllCategories();    return response;  }  @override  Future<TopProductsModel> getTopProducts() async {    final response = await api.getTopProducts();    return response;  }  @override  Future<TopCategoriesModel> getTopCategories() async {    final response = await api.getTopCategories();    return response;  }  @override  Future<ProductItemsModel> getProductItems(String slug) async {    final response = await api.getProductItems(slug);    return response;  }  @override  Future<StoresModel> getAllBranch() async{    final response = await api.getStoresModel();    return response;  }}