import 'package:dio/dio.dart';import 'package:get_it/get_it.dart';import '../data/source/local/hive_helper.dart';import '../data/source/remote/api_service.dart';import '../data/source/repository/app_repository.dart';import '../data/source/repository/app_repository_impl.dart';final getIt = GetIt.instance;final dio = Dio(BaseOptions(    contentType: 'application/json', receiveDataWhenStatusError: true));void setup() {  getIt.registerSingleton<ApiService>(ApiService(dio));  getIt.registerSingleton<AppRepository>(AppRepositoryImpl());  getIt.registerSingleton<HiveHelper>(HiveHelper());}