import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/source/local/hive_helper.dart';import '../../../../utils/product_holder.dart';part 'basket_event.dart';part 'basket_state.dart';class BasketBloc extends Bloc<BasketEvent, BasketState> {  BasketBloc() : super(BasketInitial()) {    on<GetAllProductsFromBasketEvent>((event, emit) async {      final data = HiveHelper.getAllProductsFromBasket();      print(HiveHelper.getAllProductsFromBasket().length);      emit(GetAllProductsFromBasketSate(data));    });  }}