import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_fonts/google_fonts.dart';import '../../../data/source/models/category/all_categories_model.dart';import '../../../utils/catalog.dart';import '../products/products_page.dart';import 'bloc/catalog_child_bloc.dart';class CatalogChildPage extends StatelessWidget {  final List<AllCategoriesItemChild> data;  final String name;  const CatalogChildPage({super.key, required this.data, required this.name});  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => CatalogChildBloc(),      child: BlocConsumer<CatalogChildBloc, CatalogChildState>(        listener: (context, state) {          if (state is ClickItemForSlugState) {            Navigator.push(                context,                MaterialPageRoute(                    builder: (context) =>                        ProductsPage(state.title, state.slug)));          }        },        builder: (context, state) {          return Scaffold(            appBar: AppBar(              backgroundColor: const Color(0xFFfbc100),              title: Row(                mainAxisAlignment: MainAxisAlignment.center,                children: [                ],              ),            ),            body: SafeArea(              child: ListView.builder(                  itemCount: data.length,                  itemBuilder: (context, index) {                    return Padding(                      padding: const EdgeInsets.symmetric(vertical: 3.0),                      child: catalogChildItem(data[index].name ?? '', () {                        context.read<CatalogChildBloc>().add(                            ClickItemForSlugEvent(data[index].name ?? '',                                data[index].slug ?? ''));                      }),                    );                  }),            ),          );        },      ),    );  }}