import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/source/models/category/all_categories_model.dart';import '../../../../data/source/models/slider/slider_model.dart';import '../../../../data/source/models/top_categories/top_categories_model.dart';import '../../../../data/source/models/top_products/top_products_model.dart';import '../../../../data/source/repository/app_repository.dart';import '../../../../di/di.dart';part 'home_event.dart';part 'home_state.dart';class HomeBloc extends Bloc<HomeEvent, HomeState> {  final _repository = getIt<AppRepository>();  HomeBloc() : super(HomeInitial()) {    on<GetSlidersEvent>((event, emit) async {      LoadingState();      try {        print(await _repository.getSliderData());        SliderModel data = await _repository.getSliderData();        print(data);        emit(GetSliderState(data));      } catch (e) {        print(e.toString());      }    });    on<GetAllCategoriesEvent>((event, emit) async {      LoadingState();      try {        final data = await _repository.getAllCategories();        print('bloc category ${data.data.categories!.length}');        emit(GetAllCategoriesState(data));      } catch (e) {        print(e.toString());      }    });    on<GetTopProductsEvent>((event, emit) async {      LoadingState();      try {        final data = await _repository.getTopProducts();        emit(GetTopProductsState(data));      } catch (e) {        print(e.toString());      }    });    on<GetTopCategoriesEvent>((event, emit) async {      LoadingState();      final data = await _repository.getTopCategories();      emit(GetTopCategoriesState(data));    });    on<ClickTopCategoryEvent>((event, emit) async {      emit(ClickTopCategoryState(event.title, event.slug));    });  }}