part of 'home_bloc.dart';@immutableabstract class HomeState {}class HomeInitial extends HomeState {}class GetSliderState extends HomeState {  final SliderModel data;  GetSliderState(this.data);}class GetAllCategoriesState extends HomeState {  final AllCategoriesModel data;  GetAllCategoriesState(this.data);}class GetTopCategoriesState extends HomeState {  final TopCategoriesModel data;  GetTopCategoriesState(this.data);}class GetTopProductsState extends HomeState {  final TopProductsModel data;  GetTopProductsState(this.data);}class LoadingState extends HomeState {}class ClickTopCategoryState extends HomeState {  final String title;  final String slug;  ClickTopCategoryState(this.slug, this.title);}