import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:texnomart_clone_flutter/data/source/models/branchs/filial_model.dart';import 'package:texnomart_clone_flutter/data/source/remote/api_service.dart';import '../../../../di/di.dart';part 'map_list_event.dart';part 'map_list_state.dart';class MapListBloc extends Bloc<MapListEvent, MapListState> {  final api = getIt<ApiService>();  MapListBloc() : super(MapListInitial()) {    on<GetAllMapListEvent>((event, emit) async {      final result = await api.getStoresModel();      emit(GetAllMapListState(result));    });  }}