import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import '../../../../data/source/models/product_items/product_items_model.dart';import '../../../../data/source/repository/app_repository.dart';import '../../../../di/di.dart';part 'products_event.dart';part 'products_state.dart';class ProductsBloc extends Bloc<ProductsEvent, ProductsState> {  final _repository = getIt<AppRepository>();  ProductsBloc() : super(ProductsInitial()) {    on<GetAllProductsEvent>((event, emit) async {      emit(LoadingState());      final data = await _repository.getProductItems(event.slug);      emit(GetAllProductsState(data));    });  }}