import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:texnomart_clone_flutter/utils/product_holder.dart';Widget favouriteItem(ProductHolder product, void Function() onClick) {  return Column(    children: [      Row(        children: [          Container(              width: 120,              height: 120,              decoration: BoxDecoration(                color: Colors.white,                borderRadius: BorderRadius.circular(10),              ),              child: Align(                  alignment: Alignment.center,                  child: Image.network(product.image ?? ''))),          const SizedBox(width: 15),          Column(            children: [              SizedBox(                width: 160,                child: Text(product.name ?? '',                    overflow: TextOverflow.ellipsis,                    maxLines: 2,                    textAlign: TextAlign.start,                    style: GoogleFonts.poppins(                        fontWeight: FontWeight.w400, fontSize: 15)),              ),              SizedBox(                width: 160,                child: Text("${product.axiomMonthlyPrice ?? ''} so'm",                    overflow: TextOverflow.ellipsis,                    textAlign: TextAlign.start,                    style: GoogleFonts.poppins(                        fontWeight: FontWeight.w700, fontSize: 14)),              ),              Container(                width: 160,                decoration: BoxDecoration(                    color: const Color(0x15000000),                    borderRadius: BorderRadius.circular(10)),                child: Padding(                  padding: const EdgeInsets.only(                      top: 2.0, left: 5.0, bottom: 2.0, right: 3),                  child: Text(product.salePrice.toString() ?? '',                      overflow: TextOverflow.ellipsis,                      textAlign: TextAlign.start,                      style: GoogleFonts.poppins(                          fontWeight: FontWeight.w400, fontSize: 12)),                ),              ),            ],          ),          const Spacer(),          Column(            children: [              Row(                mainAxisAlignment: MainAxisAlignment.end,                children: [                  InkWell(                      onTap: onClick,                      child: const Icon(                        Icons.favorite_rounded,                        color: Color(0xFFFFC300),                      )), // const Icon(Icons.delete_outlined),                ],              )            ],          ),          const SizedBox(width: 10)        ],      ),      const Divider(color: Color(0x15000000))    ],  );}