import 'package:flutter/material.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:google_fonts/google_fonts.dart';Widget locationItem(    String region, String title, String hour, void Function() onClick) {  return Column(    children: [      InkWell(          onTap: onClick,          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              const SizedBox(height: 10),              Row(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  Image.asset(                    'assets/images/ic_location.png',                    width: 25,                    height: 25,                  ),                  const SizedBox(width: 10),                  Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Text(                        region,                        style: TextStyle(fontSize: 14),                      ),                      SizedBox(                        width: 250,                        child: Text(                          title,                          maxLines: 2,                          style: TextStyle(fontSize: 14),                        ),                      ),                      const SizedBox(height: 15),                      Text(                        'Du-Ya($hour)',                        style: TextStyle(fontSize: 14),                      ),                    ],                  ),                  const Spacer(),                  const Icon(                    Icons.arrow_forward_ios_rounded,                    size: 20,                  )                ],              ),              const Divider()            ],          )),    ],  );}